body.script:
document.querySelector('#chat-form').addEventListener('submit', function(event) {
  event.preventDefault(); // prevent the form from refreshing the page
  var input = document.querySelector('#chat-input').value; // get the value of the input field
  var response = searchForKeywords(input); // search for keywords in the input
  document.querySelector('#chat-output').innerHTML = response; // display the response
});

function searchForKeywords(input) {
    input = input.toLowerCase()
  if (input.includes('hello')) {
    return 'Hello. I am Mrpi314';
  } else if (input.includes('bye')) {
    return 'adios amigo';
  } else if (input.includes('uc37')) {
    return 'UC37 is a chatbot that can be used to help out';
  } else if (input.includes('math')) {
    return 'UC37math is a set of simple flashcards that runs on pygame';
  } else if (input.includes('mrpi314')) {
    return "That's me!";
  } else if (input.includes('you')) {
    return "I am mrpi314";
  } else if (input.includes('mario')) {
    return "Mario Pipe is a simple Python program for a raspberry pi mean to imitate Mark Rober's hacked NES";
  } else if (input.includes('cad')) {
    return "Raspberry-pi-case-CAD is the CAD files I used to 3D print my Raspberry pi case";
  } else if (input.includes('ucprogramming')) {
    return "These are the files for a website that is still a work in progress";
  } else {
    return 'I did not understand your message';
  }
}
